function checkAnagrams(str1: string, str2: string) {
  if (str1.length !== str2.length) {
    console.log("Strings are not anagrams.");
  } else {
    let charCountStr1: any = {};
    let charCountStr2: any = {};

    // Counting the characters in first string
    for (let char of str1) {
      charCountStr1[char] ? charCountStr1[char]++ : (charCountStr1[char] = 1);
      charCountStr1[char.toUpperCase()] ? charCountStr1[char.toUpperCase()]++ : (charCountStr1[char.toUpperCase()] = 1);
    }

    // Counting the characters in second string
    for (let char of str2) {
      charCountStr2[char] ? charCountStr2[char]++ : (charCountStr2[char] = 1);
      charCountStr2[char.toUpperCase()] ? charCountStr2[char.toUpperCase()]++ : (charCountStr2[char.toUpperCase()] = 1);
    }

    // Comparing the character counts
    for (let char in charCountStr1) {
      if (!charCountStr2[char] || charCountStr1[char] !== charCountStr2[char]) {
        console.log("Strings are not anagrams.");
        return;
      }
    }
    console.log("Strings are anagrams.");
  }
}

