public static List<Integer> fibonacciSequenceUpTo(int number) {
    List<Integer> sequence = new ArrayList<Integer>();
    int first = 0, second = 1;

    while (first <= number) {
        sequence.add(first);
        int nextNumber = first + second;
        first = second;
        second = nextNumber;
    }
    return sequence;
}

