public class TrafficLight {

	// red, yellow and green color representation in integer form
	final int RED = 0;
	final int YELLOW = 1;
	final int GREEN = 2;

	private int lightColor = RED; // initialize the light to red color
	private boolean lightsOn = false; // initialize the lights as off

	public synchronized void changeLight() {

		if (lightsOn) {
			// if lights were on previously, change to yellow
			lightColor = YELLOW;
			System.out.println("Light color changed to YELLOW.");
			try {
				Thread.sleep(3000); // wait for yellow light before changing to green
			} catch (InterruptedException ex) {
				Thread.currentThread().interrupt();
			}
		}

		// change to green
		lightColor = GREEN;
		System.out.println("Light color changed to GREEN.");
		lightsOn = true;

		// wait for few seconds
		try {
			Thread.sleep(5000);
		} catch (InterruptedException ex) {
			Thread.currentThread().interrupt();
		}

		// change to red
		lightColor = RED;
		System.out.println("Light color changed to RED.");
		lightsOn = false;
	}
}

