bool is_prime(int num) {
    if (num <= 1) return false;
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0) {
            return false;
        }
    }
    return true;
}

int largest_prime(int num) {
    int prime = num - 1;
    if (is_prime(prime)) {
        return prime;
    }
    while (prime > 0) {
        prime--;
        if (is_prime(prime)) {
            return prime;
        }
    }
    return num - 1; // As a fallback, return the second last number if no prime number is found
}

int main() {
    int num;
    std::cout << "Enter a number: ";
    std::cin >> num;
    int largest_prime_num = largest_prime(num);
    std::cout << "The largest prime number less than " << num << " is: " << largest_prime_num << std::endl;
    return 0;
}
