using namespace std;

int first_duplicate(int arr[], int n) {
    unordered_map<int, int> indices;

    // Initialize the unordered map with all elements and their indices(-1 at first)
    for (int i = 0; i < n; i++) {
        indices[arr[i]] = i;
    }

    // Since we traveled through the array, the first occurrence of a duplicate should be in the middle or after
    for (int i = 0; i < n; i++) {
        if (indices[arr[i]] != i) {
            return indices[arr[i]];
        }
    }

    // If we reach this point, then the input array does not contain any duplicates
    return -1;
}

int main() {
    int arr[] = {1, 2, 3, 4, 4, 5};
    int n = sizeof(arr) / sizeof(arr[0]);

    int res = first_duplicate(arr, n);
    if (res == -1)
        cout << "There are no duplicates in the array.";
    else
        cout << "First duplicate: arr[" << res << "] = " << arr[res];

    return 0;
}

