func KnapSack(capacity int, val []int, wt []int) int {
    n := len(val)
    if capacity == 0 || n == 0 {
        return 0
    }

    if wt[0] > capacity {
        return KnapSack(capacity, val, wt[1:])
    }

    return max(val[0] + KnapSack(capacity-wt[0], val[1:], wt), KnapSack(capacity, val[1:], wt))
}

