  }

  return password;
}

function generateRandomPassword(length) {
  return new Promise((resolve, reject) => {
    crypto.randomBytes(length, (err, buf) => {
      if (err) {
        reject(err);
      } else {
        const password = buf
          .toString('base64')
          .slice(0, length)
          .replace(/\+/g, '')
          .replace(/./g, (c) => ( possible.indexOf(c) > -1 ? c : getRandomPassword(1)[0] ));
        resolve(password);
      }
    });
  });
}

