    var firstIndex = 0
    var lastIndex = list.size - 1

    while (firstIndex < list.size && lastIndex >= 0) {
        val temp = list[firstIndex]
        list[firstIndex] = list[lastIndex]
        list[lastIndex] = temp
        firstIndex++
        lastIndex--
    }

    return list
