#include <utility>

template <typename K, typename V>
class HashTable {
private:
    static const int ARRAY_SIZE = 13;
    std::vector<std::list<std::pair<K, V>>> data;

public:
    HashTable() : data(ARRAY_SIZE) {}

    V get(const K& key) {
        int index = hash(key);
        for (auto& entry : data[index]) {
            if (entry.first == key) {
                return entry.second;
            }
        }
        return V(); // Return empty value if key is not found
    }

    void put(const K& key, const V& value) {
        int index = hash(key);
        for (auto& entry : data[index]) {
            if (entry.first == key) {
                entry.second = value;
                return;
            }
        }

        data[index].emplace_back(key, value);
        handleCollision(index);
    }

private:
    int hash(const K& key) const {
        int h = 0;
        for (char c : std::to_string(key).cbegin()) {
            h = (h * 31 + c) % ARRAY_SIZE;
        }
        return h;
    }

    void handleCollision(const int index) {
        for (int i = 1; i < ARRAY_SIZE; ++i) {
            int newIndex = (index + i) % ARRAY_SIZE;
            if (data[newIndex].empty()) {
                data[newIndex].emplace_back(data[index].back().first, data[index].back().second);
                data[index].pop_back();
                return;
            }
        }
    }
};

