func largestCommonSubstring(_ str1: String, _ str2: String) -> String? {

    let length1 = str1.count
    let length2 = str2.count

    var table = Array(repeating: Array(repeating: 0, count: length2 + 1), count: length1 + 1)

    var maxLength = 0, x = 0, y = 0

    for i in 0...length1 {
        for j in 0...length2 {
            if str1[str1.index(str1.startIndex, offsetBy: i)] == str2[str2.index(str2.startIndex, offsetBy: j)] {
                if i > 0 && j > 0 {
                    table[i + 1][j + 1] = table[i][j] + 1
                }
                if table[i + 1][j + 1] > maxLength {
                    maxLength = table[i + 1][j + 1]
                    x = i
                    y = j
                }
            } else if table[i][j + 1] > table[i][j] {
                table[i + 1][j + 1] = table[i][j + 1]
            } else {
                table[i + 1][j + 1] = table[i][j]
            }
        }
    }

    if maxLength == 0 {
        return nil
    }

    var result = ""
    while x > 0 && y > 0 {
        if str1[str1.index(str1.startIndex, offsetBy: x - 1)] == str2[str2.index(str2.startIndex, offsetBy: y - 1)] {
            result = String(str1[str1.index(str1.startIndex, offsetBy: x)]) + result
            x -= 1
            y -= 1
        } else {
            x -= 1
        }
    }

    return result
}

