# Generate Caesar Cipher for Encryption
def generate_caesar_cipher(shift_key):
	caesar_cipher = {}
	ascii_lower = ord('a')
	ascii_upper = ord('A')

	for i in range(ord('a'), ord('z')+1):
		caesar_cipher[chr(i)] = chr((ord(chr(i))-ascii_lower+shift_key)%26+ascii_lower)
		caesar_cipher[chr(i).upper()] = chr((ord(chr(i).upper())-ascii_upper+shift_key)%26+ascii_upper)

	return caesar_cipher

# Decode a Caesar Cipher for an given text
def decode_caesar_cipher(text, shift_key, caesar_cipher):
	decrypted_text = ""

	for char in text:
		if char.isalpha():
			if char.islower():
				decrypted_text += caesar_cipher[char].lower()
			else:
				decrypted_text += caesar_cipher[char].upper()
		else:
			decrypted_text += char

	return decrypted_text

