var reverseWords = function(sentence) {
    let words = sentence.split(' ');
    let start = 0;
    let end = words.length - 1;

    while(start < end ){
       let temp = words[start];
       words[start] = words[end];
       words[end] = temp;
       start++;
       end--;
    }

    return words.join(' ');
};

