def is_prime(n):
    # Check if number is less than or equal to 1
    if n <= 1:
        return False
    # Iterate until i is not greater than square of n
    for i in range(2, int(n**0.5) + 1):
        # If number is divisible by i, it is not a prime number
        if n % i == 0:
            return False
    # If number is not divisible by any number less than its square, it is a prime number
    return True

