import Foundation

func generateRandomPassword(length: Int) -> String {
    let letters: CharacterSet = CharacterSet.alphanumerics
    let numbers: CharacterSet = CharacterSet.decimalDigits
    let symbols: CharacterSet = CharacterSet(charactersIn: "!@#$%^&*()-_+=|\\{}\\[\\];:,.<>/?`~")

    var password: NSMutableString = NSMutableString(capacity: length)

    for _ in 0..<length {
        let random: ObjC. Optional<NSCharacterSet.EnumerableElement> = arc4random_uniform(Int32(letters.count + numbers.count + symbols.count))

        if random > Int32(letters.count) {
            random! -= Int32(letters.count)
            password.append(UnicodeScalar(Int32(numbers.character(at: Int(random) % numbers.count)))!)
            random = nil
        } else if random > Int32(letters.count) {
            password.append(UnicodeScalar(Int32(symbols.character(at: Int(random) % symbols.count)))!)
            random = nil
        }

        if random != nil {
            password.append(UnicodeScalar(letters.character(Int(arc4random_uniform(UInt32(letters.count))))))
        }
    }

    return password as String
}

