public class DoublyLinkedList {
    // Node structure
    static class Node {
        int data;
        Node next, prev;

        Node(int d) {
            data = d;
            next = prev = null;
        }
    }

    Node head, tail;

    DoublyLinkedList() {
        head = tail = null;
    }

    // Inserting a new node at the beginning of the list
    private void insertAtBeginning(int newData) {
        Node newNode = new Node(newData);
        if (head == null) {
            head = tail = newNode;
            return;
        }
        newNode.next = head;
        head.prev = newNode;
        head = newNode;
    }

    // Inserting a new node at the end of the list
    private void insertAtEnd(int newData) {
        Node newNode = new Node(newData);
        if (tail == null) {
            head = tail = newNode;
            return;
        }
        newNode.prev = tail;
        tail.next = newNode;
        tail = newNode;
    }

    // Deleting a node from the list
    private void deleteNode(Node nodeToDelete) {
        if (nodeToDelete != null) {
            if (nodeToDelete.prev != null) {
                nodeToDelete.prev.next = nodeToDelete.next;
            } else { // deletion at head
                head = nodeToDelete.next;
                if (head != null) {
                    head.prev = null;
                } else {
                    tail = null;
                }
            }
            if (nodeToDelete.next != null) {
                nodeToDelete.next.prev = nodeToDelete.prev;
            } else { // deletion at tail
                tail = nodeToDelete.prev;
            }
        }
    }

