sub is_prime {
  my $num = shift;
  return 1 if $num <= 1; # 0 and 1 are not prime numbers

  for my $i (2..$num/2) {
    return 0 if $num % $i == 0;
  }

  return 1;
}

sub generate_primes {
  my $limit = shift;

  for my $number (2..$limit) {
    this_prime: next if exists $primes{$number}; # check hash for existing primes
    push @{$primes{$number}{'primes'}}, $number;
    for my $i (1+int(sqrt($number))) {
      next this_prime if is_prime($i) && $number % $i == 0;
    }
  }

  @{$primes{$_}{'primes'}} for values %primes;
  return @{$primes{$_}{'primes'}};
}

