    public static String caesarCipherEncryption(String text, int key, String str) {
        char[] textCharArray = text.toCharArray();
        char[] strCharArray = str.toCharArray();

        StringBuilder encryptedText = new StringBuilder();
        for (char aTextCharArray : textCharArray) {
            int index = Strings.indexOf(str, String.valueOf(aTextCharArray));
            char nextChar;
            if (index > Strings.length(str) - key) {
                nextChar = Strings.charAt(str, index - Strings.length(str) + key);
            } else {
                nextChar = Strings.charAt(str, index + key);
            }
            encryptedText.append(nextChar);
        }
        return encryptedText.toString();
    }

    public static String caesarCipherDecryption(String encryptedText, int key, String str) {
        return caesarCipherEncryption(encryptedText, -key, str);
    }
